targets.expr:
sum by (name, id) (
  container_memory_working_set_bytes{id=~"^[a-f0-9]{64}$"} 
  * on (id) group_left(name) 
  docker_container_info{project=~"$project", service=~"$service", name=~"$container"}
)

sum by (name, id) (
  container_memory_usage_bytes{id=~"^[a-f0-9]{64}$"} 
  * on (id) group_left(name) 
  docker_container_info{project=~"$project", service=~"$service", name=~"$container"}
)

sum by (name) (

    container_memory_mapped_file{id=~"^[a-f0-9]{64}$"} * on (id) group_left(name) docker_container_info{project=~"$project", service=~"$service", name=~"$container"}
    or
    container_memory_rss{id=~"^[a-f0-9]{64}$"} * on (id) group_left(name) docker_container_info{project=~"$project", service=~"$service", name=~"$container"}

)
